import axios from "axios";
import React, { useEffect, useState } from "react";
import { useLocation, useParams } from "react-router-dom";
import { logEvent } from "../services/api";
import TimerComponent from "./TimerComponent";

const TestPageComponent = () => {
  const { attemptId } = useParams();
  const [attemptDetails, setAttemptDetails] = useState(null);
  const location = useLocation();

  const handleSubmit = () => {
    logEvent(attemptId, "TIMER_EXPIRED");
  };
  useEffect(() => {
    fetchUserDetails();
    logEvent(attemptId, "TIMER_STARTED");
  }, [attemptId]);

  const fetchUserDetails = async () => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/attempt/${attemptId}`,
      );
      setAttemptDetails(response.data);
    } catch (error) {
      console.error("Error fetching attempt details:", error);
    }
  };
  console.log(attemptDetails);

  useEffect(()=>{
    const handleVisisbilityChange = () => {
      if(document.hidden){
        logEvent(attemptId, "TAB_HIDDEN");
        console.log("Tab_hidden");
        
      } else {
        logEvent(attemptId, "TAB_VISIBLE");
        console.log("Tab_visible");
      }
  }
  document.addEventListener("visibilitychange", handleVisisbilityChange);
  return () => {
    document.removeEventListener("visibilitychange", handleVisisbilityChange);
  };
}, [attemptId]);

  return (
    <div>
      <h1>Test Page</h1>
      <TimerComponent
        duration={location.state?.endTime || 0}
        attemptId={attemptDetails?.attemptId}
        onExpire={handleSubmit}
      />
      <button onClick={handleSubmit}>Submit Test</button>
    </div>
  );
};

export default TestPageComponent;
