import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./HomeComponent.css";

const STORAGE_KEY = "assessment_user_id";

const HomeComponent = () => {
  const [loading, setLoading] = useState(false);
  const [isCompleted, setIsCompleted] = useState(false);
  const [errorMessage, setErrorMessage] = useState(null);
  const [userId, setUserId] = useState(() => {
    return sessionStorage.getItem(STORAGE_KEY) || "";
  });
  const nav = useNavigate();

  useEffect(() => {
    if (!userId.trim()) return;

    const checkAttemptStatus = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5000/api/attempt/status/${userId}`
        );

        const aid = response.data.attemptId;
        if (response.data.status === "EXPIRED" && aid) {
          setIsCompleted(true);
          nav(`/result/${aid}`);
        } else if (response.data.status === "IN_PROGRESS" && aid) {
          nav(`/test/${aid}`);
        }
      } catch (error) {
        console.log("No existing attempt found");
      } finally {
        setLoading(false);
      }
    };

    checkAttemptStatus();
  }, [nav, userId]);

  const handleStartTest = async () => {
    const id = userId.trim();
    if (!id) {
      alert("Please enter your User ID or email.");
      return;
    }

    sessionStorage.setItem(STORAGE_KEY, id);
    setUserId(id);

    try {
      setLoading(true);

      const response = await axios.post(
        "http://localhost:5000/api/attempt/start",
        {
          userId: id,
          durationInSeconds: 6000,
        },
      );

      console.log("Attempt started:", response.data);

      if (response.data.status === "EXPIRED") {
        nav(`/result/${response.data.attemptId}`, { replace: true });
        return;
      }
      nav(`/test/${response.data.attemptId}`, {
        replace: true,
        state: { endTime: response.data.endTime },
      });
    } catch (error) {
      console.error("Error starting test:", error);
      setErrorMessage(null);

      const data = error.response?.data;

      if (data?.message === "Assessment already in progress") {
        nav(`/test/${data.attemptId}`, {
          state: {
            attemptId: data.attemptId,
            endTime: data.endTime,
          },
        });
        return;
      }

      if (data?.message === "Assessment already completed" && data?.attemptId) {
        setIsCompleted(true);
        setErrorMessage({
          text: "You have already completed this assessment.",
          attemptId: data.attemptId,
        });
        return;
      }

      setErrorMessage({
        text: data?.message || "Something went wrong. Please try again.",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleSwitchUser = () => {
    sessionStorage.removeItem(STORAGE_KEY);
    setUserId("");
    setIsCompleted(false);
    setErrorMessage(null);
  };

  if (loading) {
    return (
      <div className="home__loading">
        <div style={{ textAlign: "center" }}>
          <div className="home__spinner" />
          <p className="home__loading-text">Starting assessmentâ€¦</p>
        </div>
      </div>
    );
  }

  return (
    <div className="home">
      <div className="home__card">
        <header className="home__header">
          <h1 className="home__title">Assessment Portal</h1>
          <p className="home__subtitle">
            Enter your ID or email to begin or resume your test
          </p>
        </header>

        {errorMessage && (
          <div className="home__alert">
            <p>{errorMessage.text}</p>
            {errorMessage.attemptId && (
              <button
                type="button"
                className="home__btn home__btn--secondary"
                onClick={() => nav(`/result/${errorMessage.attemptId}`)}
              >
                View result
              </button>
            )}
          </div>
        )}

        <label className="home__field" htmlFor="userId">
          <span className="home__label">User ID / Email</span>
          <input
            id="userId"
            type="text"
            className="home__input"
            value={userId}
            onChange={(e) => {
              setUserId(e.target.value);
              setErrorMessage(null);
            }}
            placeholder="e.g. john.doe@company.com or Employee ID"
            disabled={!!userId && isCompleted}
          />
        </label>

        <div className="home__actions">
          <button
            type="button"
            className="home__btn home__btn--primary"
            onClick={() => handleStartTest()}
            disabled={isCompleted}
          >
            {isCompleted ? "Test Completed" : "Start Test"}
          </button>
          {userId && (
            <button
              type="button"
              className="home__btn home__btn--secondary"
              onClick={handleSwitchUser}
            >
              Switch user
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default HomeComponent;
