import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
const HomeComponent = () => {
  const [loading, setLoading] = useState(false);
  const nav = useNavigate();

  const handleStartTest = async () => {
    try {
      setLoading(true);

      const response = await axios.post(
        "http://localhost:5000/api/attempt/start",
        {
          userId: "user123",
          durationInSeconds: 6000,
        },
      );

      if (response.status === "EXPIRED") {
        nav(`/result/${response.attemptId}`, { replace: true });
        return;
      }
      nav(`/test/${response.data.attemptId}`, {
        replace: true,
        state: { endTime: response.data.endTime },
      });
    } catch (error) {
      console.error("Error starting test:", error);

      const data = error.response?.data;

      if (data?.message === "Assessment already in progress") {
        // ðŸ‘‡ Resume existing attempt
        nav(`/test/${data.attemptId}`, {
          state: {
            attemptId: data.attemptId,
            endTime: data.endTime,
          },
        });
      }
    } finally {
      setLoading(false);
    }
  };
  if (loading) {
    return <h1>Loading...</h1>;
  }
  return (
    <div>
      <button onClick={() => handleStartTest()}>Start Test</button>
    </div>
  );
};

export default HomeComponent;
