import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "../../axios/axiosConfig";

export default function AdminAnswer() {
  const { attemptId } = useParams();
  const [answers, setAnswers] = useState([]);

//   useEffect(() => {
//     axios.get(`/admin/attempt/${attemptId}/answers`)
//       .then(res => {
//         console.log(res.data);
//         setAnswers(res.data.answers);
//       })
//       .catch(err => {
//         console.error(err);
//       });
//   }, [attemptId]);

useEffect(() => {
    axios.get(`/admin/attempt/${attemptId}/answers`)
      .then(res => {
        const answerObj = res.data.answers;
  
        // convert object → array
        const answerArray = Object.entries(answerObj);
  
        setAnswers(answerArray);
      });
  }, [attemptId]);
  
  return (
    <div>
      <h2>Descriptive Answers</h2>

      {/* {answers.length === 0 ? (
        <p>No Answers Submitted</p>
      ) : (
        answers.map((a, index) => (
          <div key={a._id} style={{
            border: "1px solid #ccc",
            padding: "10px",
            marginBottom: "10px"
          }}>
            <h4>Question {index + 1}</h4>

            {a.textAnswer?.trim() ? (
              <p>{a.textAnswer}</p>
            ) : (
              <p style={{ color: "gray" }}>Not Attempted</p>
            )}
          </div>
        ))
      )} */}


{answers.length === 0 ? (
  <p>No Answers Submitted</p>
) : (
  answers.map(([questionId, text], index) => (
    <div key={questionId} style={{
      border: "1px solid #ccc",
      padding: "10px",
      marginBottom: "10px"
    }}>
      <h4>{questionId}</h4>

      {text?.trim() ? (
        <p>{text}</p>
      ) : (
        <p style={{ color: "gray" }}>Not Attempted</p>
      )}
    </div>
  ))
)}

    </div>
  );
}
