// AdminAudit.jsx

import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "../../axios/axiosConfig";

export default function AdminAudit() {
  const { attemptId } = useParams();
  const [events, setEvents] = useState([]);

useEffect(() => {
    axios.get(`/attempt/${attemptId}/events`)
      .then(res => {
        const sorted = res.data.events.sort(
          (a, b) => new Date(a.timestamp) - new Date(b.timestamp)
        );
  
        setEvents(sorted);
      });
  }, [attemptId]);
  

  const violationTypes = [
    "TAB_SWITCH",
    "FULLSCREEN_EXIT",
    "COPY_ATTEMPT",
    "DEVTOOLS_ATTEMPT",
    "TAB_HIDDEN",
    "TAB_VISIBLE",
    "REFRESH_DETECTED",
    "RIGHT_CLICK_BLOCKED",
    "VIOLATION_LIMIT_REACHED",
    "KEYBOARD_SHORTCUT_BLOCKED"
  ];

  const violationCount = events.filter(e =>{
    return violationTypes.includes(e.eventType)
  }
  ).length;

  return (
    <div>
      <h2>Audit Trail</h2>

      <p><strong>Total Violations:</strong> {violationCount}</p>

      <table border="1">
        <thead>
          <tr>
            <th>Time</th>
            <th>Event</th>
            <th>Question</th>
          </tr>
        </thead>
        <tbody>
          {events.map((e, index) => (
            <tr key={index}>
              <td>{new Date(e.timestamp).toLocaleString()}</td>
              <td>{e.eventType}</td>
              <td>{e.questionId || "-"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
