import { useEffect, useRef } from "react";
import { logEvent } from "../services/api";

const useProctoring = ({ attemptId, onViolationLimitReached }) => {
  const violationCount = useRef(0);

  const getBrowserMetadata = () => ({
    userAgent: navigator.userAgent,
    platform: navigator.platform,
    language: navigator.language,
    screenResolution: `${window.screen.width}x${window.screen.height}`,
    onlineStatus: navigator.onLine,
  });

  useEffect(() => {
    const handleFullscreenChange = () => {
      if (!document.fullscreenElement) {
        violationCount.current++;

        logEvent(
          {
            attemptId,
            eventType: "FULLSCREEN_EXIT",
            questionId: null,
            metadata: getBrowserMetadata(),
          },
          { useBeacon: true }
        );
        console.log("Full screen Exit – event sent (proctoring)");

        checkViolationLimit();
      }
    };

    document.addEventListener("fullscreenchange", handleFullscreenChange);

    return () =>
      document.removeEventListener("fullscreenchange", handleFullscreenChange);
  }, [attemptId]);

  //   useEffect(() => {
  //     const handleVisibilityChange = () => {
  //       if (document.visibilityState === "hidden") {
  //         violationCount.current++;

  //         logEvent(attemptId, "FOCUS_LOST", null, {
  //           visibilityState: document.visibilityState,
  //         });

  //         checkViolationLimit();
  //       }
  //     };

  //     document.addEventListener("visibilitychange", handleVisibilityChange);

  //     return () =>
  //       document.removeEventListener("visibilitychange", handleVisibilityChange);
  //   }, []);

  useEffect(() => {
    const handleVisisbilityChange = () => {
      if (document.hidden) {
        violationCount.current++;

        logEvent({
          attemptId,
          eventType: "TAB_HIDDEN",
          questionId: null,
          metadata: {
            focusState: document.visibilityState,
            timestamp: new Date().toISOString(),
          },
        });

        logEvent({
          attemptId,
          eventType: "FOCUS_LOST",
          questionId: null,
          metadata: {
            visibilityState: document.visibilityState,
            timestamp: new Date().toISOString(),
          },
        });
        console.log("Tab Hidden");
        checkViolationLimit();
      } else {
        logEvent({
          attemptId,
          eventType: "TAB_VISIBLE",
          questionId: null,
          metadata: {
            timestamp: new Date().toISOString(),
            focusState: document.visibilityState,
          },
        });
        console.log("Tab_visible");
      }
    };
    document.addEventListener("visibilitychange", handleVisisbilityChange);
    return () => {
      document.removeEventListener("visibilitychange", handleVisisbilityChange);
    };
  }, []);

  useEffect(() => {
    const handleCopy = () => {
      logEvent({
        attemptId,
        eventType: "COPY_ATTEMPT",
        questionId: null,
        metadata: getBrowserMetadata(),
      });
      console.log("Copy Attempt");
    };

    const handlePaste = () => {
      logEvent({
        attemptId,
        eventType: "PASTE_ATTEMPT",
        questionId: null,
        metadata: getBrowserMetadata(),
      });
      console.log("Paste Attempt");
    };

    document.addEventListener("copy", handleCopy);
    document.addEventListener("paste", handlePaste);

    return () => {
      document.removeEventListener("copy", handleCopy);
      document.removeEventListener("paste", handlePaste);
    };
  }, []);

  useEffect(() => {
    const handleRightClick = (e) => {
      e.preventDefault();
      logEvent({
        attemptId,
        eventType: "RIGHT_CLICK_BLOCKED",
        questionId: null,
        metadata: getBrowserMetadata(),
      });
      console.log("Right Click Blocked");
    };

    const handleKeyDown = (e) => {
      // Block F12
      if (e.key === "F12") {
        e.preventDefault();
        violationCount.current++;
        logEvent({
          attemptId,
          eventType: "DEVTOOLS_ATTEMPT",
          questionId: null,
          metadata: getBrowserMetadata(),
        });
        console.log("DevTools Attempt");
        checkViolationLimit();
      }

      // Block Ctrl+C, Ctrl+V, Ctrl+U
      if (e.ctrlKey && ["c", "v", "u", "s"].includes(e.key.toLowerCase())) {
        e.preventDefault();
        logEvent({
          attemptId,
          eventType: "KEYBOARD_SHORTCUT_BLOCKED",
          questionId: null,
          metadata: {
            key: e.key,
          },
        });
      }
    };

    document.addEventListener("contextmenu", handleRightClick);
    document.addEventListener("keydown", handleKeyDown);

    return () => {
      document.removeEventListener("contextmenu", handleRightClick);
      document.removeEventListener("keydown", handleKeyDown);
    };
  }, []);

  const checkViolationLimit = () => {
    if (violationCount.current >= 3) {
      logEvent({
        attemptId,
        eventType: "VIOLATION_LIMIT_REACHED",
        questionId: null,
        metadata: getBrowserMetadata(),
      });

      if (onViolationLimitReached) {
        onViolationLimitReached();
      }
    }
  };
};

export default useProctoring;
