import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const STORAGE_KEY = "assessment_user_id";

const HomeComponent = () => {
  const [loading, setLoading] = useState(false);
  const [isCompleted, setIsCompleted] = useState(false);
  const [errorMessage, setErrorMessage] = useState(null);
  const [userId, setUserId] = useState(() => {
    return sessionStorage.getItem(STORAGE_KEY) || "";
  });
  const nav = useNavigate();

  useEffect(() => {
    if (!userId.trim()) return;

    const checkAttemptStatus = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5000/api/attempt/status/${userId}`
        );

        if (response.data.status === "EXPIRED") {
          setIsCompleted(true);
          nav(`/result/${response.data.attemptId}`);
        }

        if (response.data.status === "IN_PROGRESS") {
          nav(`/test/${response.data.attemptId}`);
        }
      } catch (error) {
        console.log("No existing attempt found");
      } finally {
        setLoading(false);
      }
    };

    checkAttemptStatus();
  }, [nav, userId]);

  const handleStartTest = async () => {
    const id = userId.trim();
    if (!id) {
      alert("Please enter your User ID or email.");
      return;
    }

    sessionStorage.setItem(STORAGE_KEY, id);
    setUserId(id);

    try {
      setLoading(true);

      const response = await axios.post(
        "http://localhost:5000/api/attempt/start",
        {
          userId: id,
          durationInSeconds: 6000,
        },
      );

      console.log("Attempt started:", response.data);

      if (response.data.status === "EXPIRED") {
        nav(`/result/${response.data.attemptId}`, { replace: true });
        return;
      }
      nav(`/test/${response.data.attemptId}`, {
        replace: true,
        state: { endTime: response.data.endTime },
      });
    } catch (error) {
      console.error("Error starting test:", error);
      setErrorMessage(null);

      const data = error.response?.data;

      if (data?.message === "Assessment already in progress") {
        nav(`/test/${data.attemptId}`, {
          state: {
            attemptId: data.attemptId,
            endTime: data.endTime,
          },
        });
        return;
      }

      if (data?.message === "Assessment already completed" && data?.attemptId) {
        setIsCompleted(true);
        setErrorMessage({
          text: "You have already completed this assessment.",
          attemptId: data.attemptId,
        });
        return;
      }

      setErrorMessage({
        text: data?.message || "Something went wrong. Please try again.",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleSwitchUser = () => {
    sessionStorage.removeItem(STORAGE_KEY);
    setUserId("");
    setIsCompleted(false);
    setErrorMessage(null);
  };

  if (loading) {
    return <h1>Loading...</h1>;
  }

  return (
    <div>
      <label htmlFor="userId">
        User ID / Email
        <input
          id="userId"
          type="text"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          placeholder="e.g. john.doe@company.com or Employee ID"
          disabled={!!userId && isCompleted}
        />
      </label>
      <button onClick={() => handleStartTest()}>
        {isCompleted ? "Test Completed" : "Start Test"}
      </button>
      {userId && (
        <button type="button" onClick={handleSwitchUser} style={{ marginLeft: 8 }}>
          Switch user
        </button>
      )}
    </div>
  );
};

export default HomeComponent;
