import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function AdminAudit() {
  const { id } = useParams();
  const [events, setEvents] = useState([]);

  useEffect(() => {
    axios.get(`/api/attempt/${id}/events`)
      .then(res => {
        const sorted = res.data.sort(
          (a, b) => new Date(a.timestamp) - new Date(b.timestamp)
        );
        setEvents(sorted);
      });
  }, [id]);

  const violationTypes = [
    "TAB_SWITCH",
    "FULLSCREEN_EXIT",
    "COPY_ATTEMPT",
    "DEVTOOLS_ATTEMPT"
  ];

  const violationCount = events.filter(e =>
    violationTypes.includes(e.eventType)
  ).length;

  return (
    <div>
      <h2>Audit Trail</h2>

      <p><strong>Total Violations:</strong> {violationCount}</p>

      <table border="1">
        <thead>
          <tr>
            <th>Time</th>
            <th>Event</th>
            <th>Question</th>
          </tr>
        </thead>
        <tbody>
          {events.map((e, index) => (
            <tr key={index}>
              <td>{new Date(e.timestamp).toLocaleString()}</td>
              <td>{e.eventType}</td>
              <td>{e.questionId || "-"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
