import axios from "axios";
import React, { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { getAttemptById, logEvent } from "../services/api";
import TimerComponent from "./TimerComponent";
import { EventLoggerProvider } from "../context/EventLoggerContext";
import { submitTest } from "../services/api";
import useProctoring from "../hooks/useProctoring";

const TestPageComponent = () => {
  const { attemptId } = useParams();
  const [attemptDetails, setAttemptDetails] = useState(null);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const location = useLocation();
  const navigate = useNavigate();

  const handleSubmit = async () => {
    if (isSubmitted) {
      console.log("Already submitted");
      return;
    }

    console.log("Auto submitting test...");

    try {
      setIsSubmitted(true);
      logEvent(attemptId, "TEST_SUBMITTED");
      await submitTest(attemptId);
      navigate(`/result/${attemptId}`, { replace: true });
      console.log("Test submitted successfully");
    } catch (error) {
      console.error("Error submitting test:", error);
    }
  };

  useEffect(() => {
    const enterFullscreen = async () => {
      try {
        await document.documentElement.requestFullscreen();
      } catch (err) {
        console.error("Fullscreen failed:", err);
      }
    };

    enterFullscreen();
  }, []);

  // ðŸ”¥ Auto submit after 3 violations
  const handleViolationLimit = async () => {
    // await submitTest(attemptId); // your submit API
    // navigate(`/result/${attemptId}`, { replace: true });
    console.log("Violation limit reached. Auto-submitting test...");
  };

  // ðŸ”¥ Activate proctoring
  useProctoring({
    attemptId,
    onViolationLimitReached: handleViolationLimit,
  });

  const getBrowserMetadata = () => ({
    userAgent: navigator.userAgent,
    platform: navigator.platform,
    language: navigator.language,
    screenResolution: `${window.screen.width}x${window.screen.height}`,
    onlineStatus: navigator.onLine,
  });

  useEffect(() => {
    fetchUserDetails();
    logEvent(attemptId, {
      eventType: "TIMER_STARTED",
      metadata: getBrowserMetadata(),
    });
  }, [attemptId]);

  useEffect(() => {
    const checkAttemptStatus = async () => {
      console.log("checkAttempt");

      try {
        const response = await getAttemptById(attemptId);

        if (response.data.status === "EXPIRED") {
          navigate(`/result/${attemptId}`);
        }

        if (response.data.status === "SUBMITTED") {
          navigate(`/result/${attemptId}`, { replace: true });
        }
      } catch (error) {
        console.error(error);
      }
    };

    checkAttemptStatus();
  }, [attemptId]);

  const fetchUserDetails = async () => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/attempt/${attemptId}`,
      );
      setAttemptDetails(response.data);
    } catch (error) {
      console.error("Error fetching attempt details:", error);
    }
  };
  console.log(attemptDetails);

  return (
    <div>
      <EventLoggerProvider attemptId={attemptId}>
        {attemptDetails?.attemptId && (
          <TimerComponent
            attemptId={attemptDetails.attemptId}
            duration={location.state?.endTime || 0}
            isSubmitted={isSubmitted}
            onExpire={handleSubmit}
          />
        )}
      </EventLoggerProvider>
      <button disabled={isSubmitted} onClick={() => handleSubmit(attemptId)}>
        submit
      </button>
    </div>
  );
};

export default TestPageComponent;
