import axios from "axios";
import React, { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { getAttemptById, logEvent } from "../services/api";
import TimerComponent from "./TimerComponent";
import { EventLoggerProvider } from "../context/EventLoggerContext";
import { submitTest } from "../services/api";

const TestPageComponent = () => {
  const { attemptId } = useParams();
  const [attemptDetails, setAttemptDetails] = useState(null);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const location = useLocation();
  const navigate = useNavigate();

  const handleSubmit = async () => {
    if (isSubmitted) {
      console.log("Already submitted");
      return;
    }

      console.log("Auto submitting test...");

    try {
      setIsSubmitted(true);
      logEvent(attemptId, "TEST_SUBMITTED");
      await submitTest(attemptId);
      navigate(`/result/${attemptId}`, { replace: true });
      console.log("Test submitted successfully");
    } catch (error) {
      console.error("Error submitting test:", error);
    }
  };
  useEffect(() => {
    fetchUserDetails();
    logEvent(attemptId, "TIMER_STARTED");
  }, [attemptId]);

  useEffect(() => {
    const checkAttemptStatus = async () => {
      console.log("checkAttempt");

      try {
        const response = await getAttemptById(attemptId);

        const { status, attemptId } = response.data;

        if (status === "EXPIRED") {
          navigate(`/result/${attemptId}`);
        }

        if (status === "SUBMITTED") {
          navigate(`/result/${attemptId}`, { replace: true });
        }
      } catch (error) {
        console.error(error);
      }
    };

    checkAttemptStatus();
  }, [attemptId]);

  const fetchUserDetails = async () => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/attempt/${attemptId}`,
      );
      setAttemptDetails(response.data);
    } catch (error) {
      console.error("Error fetching attempt details:", error);
    }
  };
  console.log(attemptDetails);

  useEffect(() => {
    const handleVisisbilityChange = () => {
      if (document.hidden) {
        logEvent(attemptId, "TAB_HIDDEN");
        console.log("Tab_hidden");
      } else {
        logEvent(attemptId, "TAB_VISIBLE");
        console.log("Tab_visible");
      }
    };
    document.addEventListener("visibilitychange", handleVisisbilityChange);
    return () => {
      document.removeEventListener("visibilitychange", handleVisisbilityChange);
    };
  }, [attemptId]);

  return (
    <div>
      <EventLoggerProvider attemptId={attemptId}>
        {attemptDetails?.attemptId && (
          <TimerComponent
            attemptId={attemptDetails.attemptId}
            duration={location.state?.endTime || 0}
            isSubmitted={isSubmitted}
            onExpire={handleSubmit}
          />
        )}
      </EventLoggerProvider>
      <button disabled={isSubmitted} onClick={() => handleSubmit(attemptId)}>
        submit
      </button>
    </div>
  );
};

export default TestPageComponent;
