import React, { useEffect, useRef, useState } from "react";
import { logEvent } from "../services/api";

const TimerComponent = ({ duration, attemptId, onExpire, isSubmitted }) => {
  const endTime = new Date(duration);
  const WARNING_THRESHOLDS = [300, 60]; 
const [triggeredWarnings, setTriggeredWarnings] = useState([]);


  const calculateTimeLeft = () => {
    const now = new Date();
    const diff = endTime - now;
    return Math.max(0, Math.floor(diff / 1000));
  };

  const [timeLeft, setTimeLeft] = useState(calculateTimeLeft());

const timerRef = useRef(null);
  const hasExpiredRef = useRef(false);

  useEffect(() => {
  WARNING_THRESHOLDS.forEach((threshold) => {
    if (
      timeLeft === threshold &&
      !triggeredWarnings.includes(threshold)
    ) {
      alert(`Only ${threshold / 60} minutes left!`);

      setTriggeredWarnings((prev) => [...prev, threshold]);
    }
  });
}, [timeLeft, triggeredWarnings]);


  // ðŸŸ¢ Start Timer (only once)
  useEffect(() => {
    if (!duration) return;
    if (isSubmitted) return;

    // Log timer started
    logEvent(attemptId, "TIMER_STARTED");

    timerRef.current = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(timerRef.current);
  }, []);

  // ðŸ”´ Stop timer if manually submitted
  useEffect(() => {
    if (isSubmitted) {
      clearInterval(timerRef.current);
    }
  }, [isSubmitted]);

  // ðŸ”¥ Handle Expiration (only once)
  useEffect(() => {
    if (timeLeft <= 0 && !hasExpiredRef.current) {
      hasExpiredRef.current = true;

      clearInterval(timerRef.current);

      logEvent(attemptId, "TIMER_EXPIRED");
      logEvent(attemptId, "AUTO_SUBMISSION_TRIGGERED");

      onExpire();
    }
  }, [timeLeft]);

  const formatTime = (seconds) => {
  const minutes = Math.floor(seconds / 60);
  const secs = seconds % 60;
  return `${minutes}:${secs < 10 ? "0" : ""}${secs}`;
};

  return <div>TimerComponent: {formatTime(timeLeft)} s</div>;
};

export default TimerComponent;
